// src/arrays/array-rotate/array-rotate.ts
var arrayRotateMutable = (array, offset) => {
  const { length } = array;
  if (!length) return array;
  const mod = (offset % length + length) % length;
  const toAppend = array.splice(0, mod);
  array.push(...toAppend);
  return array;
};
var arrayRotate = (array, offset) => {
  return arrayRotateMutable([...array], offset);
};

export {
  arrayRotateMutable,
  arrayRotate
};
